import React, {Component} from 'react';
import { ProductListStateType, responseJSONType } from "./productTypes";
import fetchProductDetail from "../../api_calls/productDetail";

export default class ProductDetail extends Component<any, ProductListStateType>{
    state: ProductListStateType = {
        page: 1,
        perPage: 4,
        totalProducts: 0,
        productList: [],
        searchQuery: ""
      }

    async componentDidMount(){
        let id = this.props.match.params.product_id;
        try {
            let result = await fetchProductDetail(id)
            let resultJSON: responseJSONType = await result.json()
            console.log(resultJSON)
            if (result.ok) {
              this.setState({ 
                productList: resultJSON.products
              })
            } else {
              // Handle your error here
            }
          } catch(err) {
            // Handle your error here
          }
    }

    render(){

        const check = this.state.productList ? (
            <div className="check">
              <h4 className="center">{this.state.productList}</h4>
            </div>
        ) : (
            <div className="center">Loading post...</div>
        );
        return (
            <div className="container">
              {check}
            </div>
        )
    }
}
